/*jshint unused:false */
/*global tinymce:true */

/**
 * zotop 扩展工具插件
 */
tinymce.PluginManager.add('zotop_tools', function(editor, url) {	
	
	zotop_tools(editor, editor.settings.tools);

	function zotop_tools(editor, tools){
	    
	    $.each(tools, function(index, tool){
	    	
	        editor.addButton(index, {
	            text: tool.text,
	            title: tool.title || tool.text,
	            icon: tool.icon,
	            onclick: function(){

	                var dialog = $.dialog({
	                    id           : 'insert-html',
	                    url          : tool.url,
	                    title        : tool.text,
	                    width        : tool.width || '90%',
	                    height       : tool.height || '80%',
			            padding      : 0,
			            ok           : $.noop,
			            cancel       : $.noop,
			            oniframeload : function() {
			                this.loading(false);
			            },            
	        			opener       :window
	                }, true).loading(true); ;

	                dialog.addEventListener('close', function(){
	                		var data = this.returnValue;
	                        console.log(typeof(data));

	                        if ( typeof(data) == 'string' ){
	                            editor.insertContent(data);
	                            return true;
	                        }
	                      
	                        var html='';

	                        for (var i=0; i<data.length; i++) {
	                            var name        = data[i].name;
	                            var url         = data[i].url;
	                            var description = data[i].description;
	                            var ext         = data[i].ext || url.replace(/.+\./,"");

	                            
	                            if ( ext == 'jpg' || ext == 'jpeg' || ext == 'png' || ext == 'gif' || ext == 'bmp'){
	                                html += '<p><img src="'+url+'" alt="'+(description||name)+'" class="insert-image"/></p>';
	                            } else if ( ext =='swf' ){
	                                html += '<p><embed quality="high" src="'+url+'" type="application/x-shockwave-flash" allowScriptAccess="always" allowFullScreen="true" mode="transparent" width="500" height="400"></embed></p>';
	                            }else{
	                                html += '<a href="'+url+'" title="'+(description||name)+'" target="_blank">'+name+'</a>';
	                            }
	                        }

	                        editor.insertContent(html);
	                        return true;
	                    });
	            }
	        });

	    });
	}	
});
